import {BrowserRouter as Router, Switch, Route, Redirect} from "react-router-dom";
import { useState } from 'react';
import Home from './home'
import Signin from './home/login'
import Signup from './home/signup'

function App() {
  const [currentUser, setUser] = useState('123')
  return (
    <Router>
      {currentUser==='123' ? (<Switch>
            <Route exact path='/' component={Home}/>
            <Route exact path='/signin' component={Signin}/>
            <Route exact path='/signup' component={Signup}/>
            <Redirect to="/"/>
            </Switch>): 
            (<Switch>
            {(currentUser.type==="student") ? <Redirect to="/studentdashboard"/>:(currentUser.type==="company")? <Redirect to="/companydashboard"/>: <Redirect to="/admin"/>}
            </Switch>)}
    </Router>
  );
}

export default App;
