import { useHistory } from "react-router";
import Card from '../course_card'
import './index.css'
import { useEffect, useState } from 'react';

function Choice(props) {
    let history = useHistory();
    let token = localStorage.getItem('Token')
    let previousCourse = props.location.state.courseID;

    console.log(previousCourse)
    const [courseCR, setCourseCR] = useState([])

    useEffect(() => {
        const url = `http://34.126.85.190:8080/course-CR?course_ID=${previousCourse}`;
        const request = new Request(url, {
              method: "get",
              body: null,
              headers:new Headers ({
              'Token': token,
              'Accept': "application/json, text/plain, */*",
              "Content-Type": "application/json"
              })
          })      
          fetch(request).then((res) => {
              return res.json()
          }).then(json => {
              console.log(json)
              setCourseCR(json.course_cr_list) 
          }).catch(error => {
              console.log(error);
          });
    }
  , [])

    return (
        <div className="professorContainer">
            <div className="professorNav">
                <span className="logOut" onClick={()=>history.push("/")}>
                    Log out
                </span>
                <span className="returnDashboard" onClick={()=>history.push("/dashboard")}>
                    Dashboard
                </span>
                <span className="createProfComment" onClick={()=>history.push('/courserate')}>
                    Create new rate and comment
                </span>
            </div>
            <h2 className="title">The comments on this course</h2>
            <div className="profCommentArea">
                { courseCR.map((course) => { return <Card name={course.professorName} score={course.rating} comment={course.comment} /> }) }  
            </div>
        </div>
    );
  }

  export default Choice;
